import './App.css';
import Events from './components/Events'
import CreateEvent from './components/CreateEvent'
import Saved from "./components/Saved"
import NavBar from "./components/NavBar"

import React, { Component } from 'react'
import { BrowserRouter as Router, Route } from 'react-router-dom'

export class App extends Component {

  state = {
    events: [{
      name: 'Welcome Week Parade',
      location: 'MUSC',
      food: 'HotDogs',
      pic: '1.jpg',
      id: 2
    },
    ],

    savedEvents: []
  }

  saveEvent = (event) => {
    this.setState( {savedEvents: [...this.state.savedEvents, event]})
	};

  render() {
    return (
      <Router>
        <div>
          <NavBar />
          <Route exact path="/" render={props => (
            <React.Fragment>
              <Events events={this.state.events} saveEvent={this.saveEvent} />
            </React.Fragment>
          )} />
          <Route path="/Create" component={CreateEvent} />
          <Route path="/Saved" render={props => (
              <Saved  savedEvents={this.state.savedEvents}/>
          )} /> 
        </div>
      </Router>
    )
  }
}

export default App
