import React, { Component } from 'react'
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import Button from '@material-ui/core/Button';
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';
import { withStyles } from '@material-ui/core/styles';


const btnStyle = {
    color: '#fff',
}

export class Event extends Component {
    infoClick = (e) => {
        e.preventDefault();
        console.log(this.props.event.name)
    }

    saveClick = (e) => {
        e.preventDefault();
        console.log("clicked save")
        this.props.saveEvent(this.props.event)
    }

    render() {
        return (
            <GridListTile key={this.props.event.name} cols={1}>
                <img src={this.props.event.pic} alt="test" />

                <GridListTileBar title={this.props.event.name} subtitle={this.props.event.location}
                    actionIcon={
                        <div>

                            <Popup trigger={<Button color='secondary' style={btnStyle}>More Info</Button>} modal>
                                <span>
                                    <h1>Details</h1>
                                    {this.props.event.name}<br/>
                                    {this.props.event.location}<br/>
                                    {this.props.event.food}
                                </span>
                            </Popup>
                            <Button color='secondary' style={btnStyle} onClick={this.saveClick}>Add to Saved</Button>
                        </div>
                    } />
            </GridListTile>
        )
    }
}

export default Event
