import './App.css';
import Events from './components/Events'
import CreateEvent from './components/CreateEvent'
import Saved from "./components/Saved"
import NavBar from "./components/NavBar"

import React, { Component } from 'react'
import { BrowserRouter as Router, Route } from 'react-router-dom'

import {v4 as uuid} from 'uuid';

export class App extends Component {

  state = {
    events: [{
      name: 'Welcome Week Parade',
      location: 'MUSC',
      food: 'HotDogs',
      pic: '1.jpg',
      id: uuid()
    },
    {
      name: 'Event2',
      location: 'Location 2',
      food: 'Food 2',
      pic: '2.jpg',
      id: uuid()
    },
    {
      name: 'Event3',
      location: 'Location 3',
      food: 'Food 3',
      pic: '3.jpg',
      id: uuid()
    },
    {
      name: 'Event4',
      location: 'Location 4',
      food: 'Food 4',
      pic: '4.jpg',
      id: uuid()
    },
    {
      name: 'Event5',
      location: 'Location 5',
      food: 'Food 5',
      pic: '5.jpg',
      id: uuid()
    },
    {
      name: 'Event6',
      location: 'Location 6',
      food: 'Food 6',
      pic: '6.jpg',
      id: uuid()
    },
    ],

    savedEvents: [
    ],
  }

  saveEvent = (event) => {
    if(!this.state.savedEvents.includes(event)){
      this.setState( {savedEvents: [...this.state.savedEvents, event]})
      console.log("Added Event")
    }
	};

  render() {
    return (
      <Router>
        <div>
          <NavBar />
          <Route exact path="/" render={props => (
            <React.Fragment>
              <Events events={this.state.events} saveEvent={this.saveEvent} />
            </React.Fragment>
          )} />
          <Route path="/Create" component={CreateEvent} />
          <Route path="/Saved" render={props => (
              <Saved  savedEvents={this.state.savedEvents}/>
          )} /> 
        </div>
      </Router>
    )
  }
}

export default App
