import React, { Component } from 'react'
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import Button from '@material-ui/core/Button';
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';
import { withStyles } from '@material-ui/core/styles';


const btnStyle = {
    color: '#fff',
}

const modal = {
    fontSize: "12px"
}

const header = {
    width: "100%",
    borderBottom: "1px solid gray",
    fontSize: "18px",
    textAlign: "center",
    padding: "5px",
}

export class Event extends Component {
    infoClick = (e) => {
        e.preventDefault();
        console.log(this.props.event.name)
    }

    saveClick = (e) => {
        e.preventDefault();
        console.log("clicked save")
        this.props.saveEvent(this.props.event)
    }

    render() {
        return (
            <GridListTile key={this.props.event.name} cols={1}>
                <img src={this.props.event.pic} alt="test" />

                <GridListTileBar title={this.props.event.name} subtitle={this.props.event.location}
                    actionIcon={
                        <div>

                            <Popup
                                trigger={<Button color='secondary' style={btnStyle}>More Info</Button>}
                                modal
                                nested
                            >
                                {close => (
                                    <div className="modal" style={modal}>
                                        <button className="close" onClick={close}>
                                            &times;
                                        </button>
                                        <div className="header" style={header}> Modal Title </div>
                                        <div className="content">
                                            {' '}
                                        Lorem ipsum dolor sit amet consectetur adipisicing elit. Atque, a nostrum.
                                        Dolorem, repellat quidem ut, minima sint vel eveniet quibusdam voluptates
                                        delectus doloremque, explicabo tempore dicta adipisci fugit amet dignissimos?
                                        <br />
                                        Lorem ipsum dolor sit amet, consectetur adipisicing elit. Consequatur sit
                                        commodi beatae optio voluptatum sed eius cumque, delectus saepe repudiandae
                                        explicabo nemo nam libero ad, doloribus, voluptas rem alias. Vitae?
                                        </div>
                                        <div className="actions">
                                            <Popup
                                                trigger={<button className="button"> Trigger </button>}
                                                position="top center"
                                                nested
                                            >
                                                <span>
                                            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Beatae
                                            magni omnis delectus nemo, maxime molestiae dolorem numquam
                                            mollitia, voluptate ea, accusamus excepturi deleniti ratione
                                            sapiente! Laudantium, aperiam doloribus. Odit, aut.
            </span>
                                            </Popup>
                                            <button
                                                className="button"
                                                onClick={() => {
                                                    console.log('modal closed ');
                                                    close();
                                                }}
                                            >
                                                close modal
                                                     </button>
                                        </div>
                                    </div>
                                )}
                            </Popup>
);
                            <Button color='secondary' style={btnStyle} onClick={this.saveClick}>Add to Saved</Button>
                        </div>
                    } />
            </GridListTile>
        )
    }
}

export default Event
