import React, { Component } from 'react'
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import Button from '@material-ui/core/Button';
import { withStyles } from '@material-ui/core/styles';


const btnStyle = {
    color: '#fff',
}

export class Event extends Component {
    infoClick = (e) => {
        e.preventDefault();
        console.log(this.props.event.name)
    }

    saveClick = (e) => {
        e.preventDefault();
        console.log("clicked save")
        this.props.saveEvent(this.props.event)
    }

    render() {
        return (
            <GridListTile key={this.props.savedEvent.name} cols={1}>
                <img src={this.props.savedEvent.pic} alt="test" />

                <GridListTileBar title="FOOD" subtitle="MUSC" 
                    actionIcon={
                        <div>
                        <Button color='secondary' style={btnStyle} onClick={this.infoClick}>More Info</Button>
                        <Button color='secondary' style={btnStyle} onClick={this.saveClick}>Add to Saved</Button>
                        </div>
                    } />
                </GridListTile>
        )
    }
}

export default Event
